mapper.select:
SELECT tc.img_url AS coverUrl, td.source_id AS id, td.delivery_time,t.play_type,t.`star_rate_flag`, tu.avatar,tu.user_name AS NAME,tu.email,tu.mobile,t.category,t.title,IFNULL(AVG(th.`score`),0) AS score,COUNT(th.`course_id`) AS num FROM t_csp_delivery td INNER JOIN t_audio_course t ON td.source_id = t.id LEFT JOIN t_audio_course_detail tc ON td.source_id = tc.course_id AND tc.sort = 1 LEFT JOIN `t_csp_star_rate_history` th ON th.course_id = t.id INNER JOIN t_csp_user_info tu ON td.author_id = tu.id WHERE td.accept_id = #{userId}
AND (t.title LIKE CONCAT('%',#{keyWord}, '%') OR tu.user_name LIKE CONCAT('%',#{keyWord}, '%'))
GROUP BY t.id,th.`course_id` ORDER BY td.delivery_time DESC

SELECT td.source_id as id, t.title,t.play_type,tu.user_name as name,tl.start_time,tl.end_time FROM t_csp_delivery td INNER JOIN t_audio_course t ON td.source_id = t.id INNER JOIN t_csp_user_info tu ON td.author_id = tu.id INNER JOIN t_csp_live tl ON tl.course_id = td.source_id WHERE td.accept_id = #{userId}
AND tl.live_state != 2
ORDER BY td.delivery_time DESC
