mapper.select:
SELECT m.id, m.meet_name, m.meet_type, m.introduction, m.organizer, p.start_time, p.end_time,m.state, u.headimg FROM t_meet m LEFT JOIN t_meet_prop p on m.id = p.meet_id LEFT JOIN t_app_attention a ON a.master_id=m.owner_id LEFT JOIN t_app_user u on m.owner_id = u.id WHERE a.slaver_id=#{userId} AND m.state=#{state}
AND m.meet_type = #{meetType}
ORDER BY p.start_time DESC

SELECT mt.* FROM( SELECT m.id,m.meet_name,m.organizer,m.meet_type,m.state,p.start_time,p.end_time, sxs.prop_mode as requiredXs,sxs.prop_value as xsCredits,sxs.reward_limit as awardLimit, scrd.prop_mode as rewardCredit,scrd.prop_value as eduCredits,scrd.reward_limit as awardCreditLimit FROM t_meet m LEFT JOIN t_meet_prop p on m.id = p.meet_id LEFT JOIN t_app_attention a ON a.master_id = m.owner_id LEFT JOIN t_meet_setting sxs ON sxs.meet_id = m.id AND sxs.prop_type=1 LEFT JOIN t_meet_setting scrd ON scrd.meet_id = m.id AND scrd.prop_type=2 WHERE a.slaver_id=#{userId} AND m.state=#{state}
AND m.meet_type = #{meetType}
ORDER BY p.start_time DESC ) mt LEFT JOIN t_infinity_tree_detail d ON d.resource_id = mt.id WHERE d.infinity_id IS NULL

SELECT mt.* FROM( SELECT d.resource_id FROM t_infinity_tree_detail d LEFT JOIN t_infinity_tree t ON t.id = d.infinity_id WHERE t.id=#{preId} ) rs INNER JOIN ( SELECT m.id,m.meet_name,m.organizer,m.meet_type,m.state,p.start_time,p.end_time, sxs.prop_mode as requiredXs,sxs.prop_value as xsCredits,sxs.reward_limit as awardLimit, scrd.prop_mode as rewardCredit,scrd.prop_value as eduCredits,scrd.reward_limit as awardCreditLimit FROM t_meet m LEFT JOIN t_meet_prop p ON p.meet_id = m.id LEFT JOIN t_meet_setting sxs ON sxs.meet_id = m.id AND sxs.prop_type=1 LEFT JOIN t_meet_setting scrd ON scrd.meet_id = m.id AND scrd.prop_type=2 ) mt ON mt.id = rs.resource_id WHERE mt.state=#{state}
AND m.meet_type = #{meetType}
ORDER BY mt.start_time DESC

SELECT vmf.* FROM t_recommend r INNER JOIN v_meet_folder_final vmf ON vmf.id = r.resource_id WHERE r.fixed = false
AND r.rec_flag = true AND r.rec_type = 2
ORDER BY rand() limit #{randLimit}

SELECT vm.* FROM v_meet_folder_final vm INNER JOIN t_meet_audio ma on vm.id = ma.meet_id INNER JOIN t_app_user u on u.id = vm.user_id AND u.test_flag != true WHERE vm.type = 1 AND vm.state BETWEEN 1 AND 3
AND ( vm.meet_type like CONCAT(#{depart}, '%') OR vm.city LIKE CONCAT(#{city}, '%') OR vm.province like CONCAT(#{province}, '%') )
ORDER BY rand() limit #{randLimit}

select tml.* from t_meet_lecturer tml INNER JOIN t_infinity_tree_detail td ON td.resource_id = tml.meet_id
INNER JOIN v_infinity v on v.level1_id = td.infinity_id OR v.level2_id = td.infinity_id
WHERE v.level1_id = #{folderId} GROUP BY tml.meet_id LIMIT #{max}
