mapper.select:
SELECT r.*,m.meet_name,m.state
FROM t_recommend r
INNER JOIN v_meet_folder_final m
ON r.resource_id = m.id
WHERE 1 = 1

    AND m.meet_name LIKE  CONCAT('%',#{meetName},'%')


    AND r.rec_type =#{recType}


    AND r.rec_flag = #{recFlag}


    AND r.sort = #{sort}

ORDER BY r.sort
