mapper.select:
SELECT u.*, a.store AS active_store, count(att.id) AS fans FROM t_app_user u LEFT JOIN t_active_store a ON a.id = u.id LEFT JOIN t_app_attention att ON u.id = att.master_id WHERE role_id = 1 AND authed = true
AND u.tuijian = #{rcd}
AND u.test_flag = #{testFlag}
AND ( username like CONCAT('%',#{keyword}, '%') OR nickname like CONCAT('%',#{keyword}, '%') )
GROUP BY u.id ORDER BY regist_date DESC
