mapper.select:
SELECT tu.id,nickname,headimg
FROM t_app_user tu INNER JOIN t_app_attention ta ON tu.id = ta.master_id
WHERE ta.slaver_id = #{slaverId}

SELECT * FROM t_app_user WHERE 1=1
AND role_id = #{roleId}
AND authed = #{authed}
AND ( username like CONCAT('%',#{keyword}, '%') OR nickname like CONCAT('%',#{keyword}, '%') OR mobile like CONCAT('%',#{keyword}, '%') OR nickname like CONCAT('%',#{keyword}, '%') OR province like CONCAT('%',#{keyword}, '%') OR city like CONCAT('%',#{keyword}, '%') )

SELECT count(u.id) FROM t_app_user u
INNER JOIN t_app_attention a on a.slaver_id = u.id
WHERE u.unionid !='' AND a.master_id=#{userId}
